r(this.getAttribute(\'href\'));\n                    if (target) {\n                        target.scrollIntoView({ behavior: \'smooth\' });\n                    }\n                });\n            });\n\n            // Add animation to features on scroll\n            const observerOptions = {\n                threshold: 0.1,\n                rootMargin: \'0px 0px -50px 0px\'\n            };\n\n            const observer = new IntersectionObserver((entries) => {\n                entries.forEach(entry => {\n                    if (entry.isIntersecting) {\n                        entry.target.style.opacity = \'1\';\n                        entry.target.style.transform = \'translateY(0)\';\n                    }\n                });\n            }, observerOptions);\n\n            // Observe all features\n            document.querySelectorAll(\'.feature\').forEach(feature => {\n                feature.style.opacity = \'0\';\n                feature.style.transform = \'translateY(20px)\';\n                feature.style.transition = \'opacity 0.6s, transform 0.6s\';\n                observer.observe(feature);\n            });\n        });\n    </script>\n</body>\n</html>', NULL, NULL, NULL, '\"[\\\"https:\\\\\\/\\\\\\/picsum.photos\\\\\\/800\\\\\\/600?random=15\\\",\\\"https:\\\\\\/\\\\\\/picsum.photos\\\\\\/800\\\\\\/600?random=115\\\"]\"', '\"[\\\"Mobile First\\\",\\\"Cross Browser\\\",\\\"Clean Code\\\",\\\"Documentation\\\"]\"', 4, 0, 1, 0, '2025-11-30 17:05:46', '2025-12-01 23:13:06', NULL, '1.0', NULL, NULL, NULL);

